import React, { Component } from "react";
import ListItems from "./ListItems";

// Use of `state`

export default class TripsList extends Component {
  constructor(props) {
    super(props);

    // State
    this.state = {
      trips: [],
      text: "",
    };

    // Event Handler Functions
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.checkTrip = this.checkTrip.bind(this);
    this.updateWishlistTrips = this.updateWishlistTrips.bind(this);
    this.updateAccomplishedTrips = this.updateAccomplishedTrips.bind(this);
  }

  updateWishlistTrips = () => {}; // Complete

  updateAccomplishedTrips = (status) => {
    const filteredTrips = this.state.trips.filter(
      (trip) => trip.status !== status
    );
    this.setState({ accomplishedTrips: filteredTrips });
  };

  // async await (Very Important)
  checkTrip = async (id, status) => {
    await this.setState((prevState) => ({
      // Complete me
    }));
    if (status === true) {
      this.updateWishlistTrips(status);
    }
    if (status === false) {
      this.updateAccomplishedTrips(status);
    }
  };

  deleteTrip = (id) => {
    "Complete Me";
  };

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) return; // ==
    const newItem = {
      country: this.state.text,
      // >>>>>>>>>>> Missing Field
      id: new Date(),
    };
    this.setState((state) => ({
      trips: state.trips.concat(newItem),
      text: "",
    }));
  }

  render() {
    return (
      <section className="vh-100" style="background-color: #eee;">
        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-lg-9 col-xl-7">
              <div className="card rounded-3">
                <div className="card-body p-4">
                  <h2 className="text-center my-3 pb-3">Trip Tracker</h2>
                  <p className="text-center my-3 pb-3">New Trip</p>
                  <form
                    className="row row-cols-lg-auto g-3 justify-content-center align-items-center mb-4 pb-2"
                    onSubmit={this.handleSubmit}
                  >
                    <div className="col-12">
                      <div className="form-outline">
                        {/* <form id="" onSubmit={this.handleSubmit}> */}
                        <input
                          id=""
                          name=""
                          placeholder="Add a trip..."
                          type="text"
                          onChange={this.handleChange}
                          value={this.state.text}
                        />
                      </div>
                    </div>
                    <button id="" class="col-12" type="submit">
                      Add
                    </button>
                  </form>
                  <ListItems
                    trips={this.state.trips}
                    deleteTrip={this.deleteTrip}
                    checkTrip={this.checkTrip}
                  />
                  <button id="" onClick={() => {}}>
                    Trips Wishlist
                  </button>
                  <button id="" onClick={() => {}}>
                    Trips Accomplished
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
